import React, { useRef, useEffect, useState } from 'react';
import { rotateSquare, reflectSquare } from '../utils/transformUtils';
import SymmetryButton from './SymmetryButton';
import '../styles/SquareSymmetry.css';

const CANVAS_SIZE = 400; // Increase this value to make the canvas bigger
const SQUARE_SIZE = 100; // You can adjust this as needed

type SymmetryOperation = 'rotate90' | 'rotate180' | 'rotate270' | 'reflectVertical' | 'reflectHorizontal' | 'reflectDiagonal1' | 'reflectDiagonal2';

const SquareSymmetry: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [operation, setOperation] = useState<string | null>(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    // Set canvas size
    canvas.width = CANVAS_SIZE;
    canvas.height = CANVAS_SIZE;

    // Clear canvas and draw initial square
    ctx.clearRect(0, 0, CANVAS_SIZE, CANVAS_SIZE);
    const squareX = (CANVAS_SIZE - SQUARE_SIZE) / 2;
    const squareY = (CANVAS_SIZE - SQUARE_SIZE) / 2;
    ctx.fillRect(squareX, squareY, SQUARE_SIZE, SQUARE_SIZE);

    // Perform symmetry operation
    if (operation) {
      switch (operation) {
        case 'rotate90':
        case 'rotate180':
        case 'rotate270':
          rotateSquare(ctx, SQUARE_SIZE, parseInt(operation.slice(6)));
          break;
        // ... other cases remain the same
      }
    }
  }, [operation]);

  const handleSymmetryOperation = (operation: SymmetryOperation) => {
    if (!ctx) return;

    ctx.clearRect(0, 0, CANVAS_SIZE, CANVAS_SIZE);
    drawSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE);

    switch (operation) {
      case 'rotate90':
      case 'rotate180':
      case 'rotate270':
        rotateSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE, parseInt(operation.slice(6)));
        break;
      case 'reflectVertical':
        reflectSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE, 'vertical');
        drawSymmetryLine(ctx, CANVAS_SIZE / 2, 0, CANVAS_SIZE / 2, CANVAS_SIZE);
        break;
      case 'reflectHorizontal':
        reflectSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE, 'horizontal');
        drawSymmetryLine(ctx, 0, CANVAS_SIZE / 2, CANVAS_SIZE, CANVAS_SIZE / 2);
        break;
      case 'reflectDiagonal1':
        reflectSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE, 'diagonal1');
        drawSymmetryLine(ctx, 0, 0, CANVAS_SIZE, CANVAS_SIZE);
        break;
      case 'reflectDiagonal2':
        reflectSquare(ctx, CANVAS_SIZE / 2, CANVAS_SIZE / 2, SQUARE_SIZE, 'diagonal2');
        drawSymmetryLine(ctx, CANVAS_SIZE, 0, 0, CANVAS_SIZE);
        break;
    }
  };

  return (
    <div className="square-symmetry">
      <canvas ref={canvasRef} width={CANVAS_SIZE} height={CANVAS_SIZE} />
      <div className="button-container">
        <SymmetryButton onClick={() => handleSymmetryOperation('rotate90')} label="Rotate 90°" />
        <SymmetryButton onClick={() => handleSymmetryOperation('rotate180')} label="Rotate 180°" />
        <SymmetryButton onClick={() => handleSymmetryOperation('rotate270')} label="Rotate 270°" />
        <SymmetryButton onClick={() => handleSymmetryOperation('reflectVertical')} label="Vertical Reflection" />
        <SymmetryButton onClick={() => handleSymmetryOperation('reflectHorizontal')} label="Horizontal Reflection" />
        <SymmetryButton onClick={() => handleSymmetryOperation('reflectDiagonal1')} label="Diagonal Reflection 1 (45°)" />
        <SymmetryButton onClick={() => handleSymmetryOperation('reflectDiagonal2')} label="Diagonal Reflection 2 (-45°)" />
      </div>
    </div>
  );
};

export default SquareSymmetry;
