export function drawSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number) {
    ctx.fillStyle = '#3498db';
    ctx.fillRect(x - size / 2, y - size / 2, size, size);
  }

  export function drawLighterSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number) {
    ctx.fillStyle = 'rgba(52, 152, 219, 0.5)';
    ctx.fillRect(x - size / 2, y - size / 2, size, size);
  }

  export function drawSymmetryLine(ctx: CanvasRenderingContext2D, startX: number, startY: number, endX: number, endY: number) {
    ctx.beginPath();
    ctx.moveTo(startX, startY);
    ctx.lineTo(endX, endY);
    ctx.setLineDash([5, 5]);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 2;
    ctx.stroke();
    ctx.setLineDash([]);
  }

  export function drawOverlappingSquares(ctx: CanvasRenderingContext2D, size: number) {
    // Create gradient for the first square
    const gradient1 = ctx.createLinearGradient(-size/2, -size/2, size/2, size/2);
    gradient1.addColorStop(0, 'rgba(41, 128, 185, 0.7)');  // Light blue
    gradient1.addColorStop(1, 'rgba(52, 152, 219, 0.7)');  // Slightly darker blue

    // Draw the first square
    ctx.beginPath();
    ctx.rect(-size / 2, -size / 2, size, size);
    ctx.fillStyle = gradient1;
    ctx.fill();

    // Create gradient for the second square
    const gradient2 = ctx.createLinearGradient(-size/2, -size/2, size/2, size/2);
    gradient2.addColorStop(0, 'rgba(230, 126, 34, 0.7)');  // Light orange
    gradient2.addColorStop(1, 'rgba(211, 84, 0, 0.7)');    // Slightly darker orange

    // Draw the second square rotated 45 degrees
    ctx.save();
    ctx.rotate(Math.PI / 4);
    ctx.beginPath();
    ctx.rect(-size / 2, -size / 2, size, size);
    ctx.fillStyle = gradient2;
    ctx.fill();
    ctx.restore();

    // Draw outline
    ctx.beginPath();
    ctx.rect(-size / 2, -size / 2, size, size);
    ctx.rotate(Math.PI / 4);
    ctx.rect(-size / 2, -size / 2, size, size);
    ctx.rotate(-Math.PI / 4);
    ctx.strokeStyle = 'rgba(0, 0, 0, 0.3)';
    ctx.lineWidth = 2;
    ctx.stroke();
  }
