export function drawSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number) {
    ctx.fillStyle = '#3498db';
    ctx.fillRect(x - size / 2, y - size / 2, size, size);
  }

  export function drawLighterSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number) {
    ctx.fillStyle = 'rgba(52, 152, 219, 0.5)';
    ctx.fillRect(x - size / 2, y - size / 2, size, size);
  }

  export function drawSymmetryLine(ctx: CanvasRenderingContext2D, startX: number, startY: number, endX: number, endY: number) {
    ctx.beginPath();
    ctx.moveTo(startX, startY);
    ctx.lineTo(endX, endY);
    ctx.setLineDash([5, 5]);
    ctx.strokeStyle = '#e74c3c';
    ctx.lineWidth = 2;
    ctx.stroke();
    ctx.setLineDash([]);
  }

  export function drawOverlappingSquares(ctx: CanvasRenderingContext2D, size: number) {
    const halfSize = size / 2;
    const quarterSize = size / 4;

    // Create a pattern
    const patternCanvas = document.createElement('canvas');
    const patternCtx = patternCanvas.getContext('2d');
    const patternSize = 20;

    patternCanvas.width = patternSize;
    patternCanvas.height = patternSize;

    if (patternCtx) {
      patternCtx.fillStyle = 'rgba(255, 255, 255, 0.1)';
      patternCtx.fillRect(0, 0, patternSize, patternSize);
      patternCtx.strokeStyle = 'rgba(0, 0, 0, 0.1)';
      patternCtx.lineWidth = 0.5;
      patternCtx.beginPath();
      patternCtx.moveTo(0, 0);
      patternCtx.lineTo(patternSize, patternSize);
      patternCtx.moveTo(patternSize, 0);
      patternCtx.lineTo(0, patternSize);
      patternCtx.stroke();
    }

    const pattern = ctx.createPattern(patternCanvas, 'repeat');

    // Draw the shape
    ctx.beginPath();
    ctx.moveTo(0, -halfSize);
    ctx.lineTo(halfSize, -quarterSize);
    ctx.lineTo(halfSize, quarterSize);
    ctx.lineTo(quarterSize, halfSize);
    ctx.lineTo(-quarterSize, halfSize);
    ctx.lineTo(-halfSize, quarterSize);
    ctx.lineTo(-halfSize, -quarterSize);
    ctx.lineTo(-quarterSize, -halfSize);
    ctx.closePath();

    // Fill with gradient
    const gradient = ctx.createRadialGradient(0, 0, 0, 0, 0, halfSize);
    gradient.addColorStop(0, 'rgba(52, 152, 219, 0.8)');
    gradient.addColorStop(1, 'rgba(41, 128, 185, 0.8)');
    ctx.fillStyle = gradient;
    ctx.fill();

    // Apply texture
    if (pattern) {
      ctx.fillStyle = pattern;
      ctx.fill();
    }

    // Add a subtle border
    ctx.strokeStyle = 'rgba(0, 0, 0, 0.3)';
    ctx.lineWidth = 2;
    ctx.stroke();
  }

  export function drawDiagonals(ctx: CanvasRenderingContext2D, size: number) {
    ctx.beginPath();
    ctx.moveTo(-size / 2, -size / 2);
    ctx.lineTo(size / 2, size / 2);
    ctx.moveTo(size / 2, -size / 2);
    ctx.lineTo(-size / 2, size / 2);
    ctx.strokeStyle = 'rgba(255, 0, 0, 0.5)';
    ctx.stroke();
  }
