import React, { useRef, useEffect, useState } from 'react';
import { transformCross } from '../utils/transformUtils';
import '../styles/SquareSymmetry.css'; // Make sure to create this CSS file

const SquareSymmetry: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [angle, setAngle] = useState(0);
  const [size, setSize] = useState(100);
  const [reflectVertical, setReflectVertical] = useState(false);
  const [reflectHorizontal, setReflectHorizontal] = useState(false);
  const [reflectDiagonal1, setReflectDiagonal1] = useState(false);
  const [reflectDiagonal2, setReflectDiagonal2] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext('2d');
      if (ctx) {
        transformCross(ctx, size, angle, reflectVertical, reflectHorizontal, reflectDiagonal1, reflectDiagonal2);
      }
    }
  }, [angle, size, reflectVertical, reflectHorizontal, reflectDiagonal1, reflectDiagonal2]);

  const handleReset = () => {
    setAngle(0);
    setSize(100);
    setReflectVertical(false);
    setReflectHorizontal(false);
    setReflectDiagonal1(false);
    setReflectDiagonal2(false);
  };

  return (
    <div className="square-symmetry-container">
      <h1 className="title">8-Pointed Star: Islamic Geometric Pattern</h1>
      <canvas ref={canvasRef} width={400} height={400} />
      <div className="controls">
        <label>
          Angle:
          <input
            type="number"
            value={angle}
            onChange={(e) => setAngle(Number(e.target.value))}
          />
        </label>
        <label>
          Size:
          <input
            type="number"
            value={size}
            onChange={(e) => setSize(Number(e.target.value))}
          />
        </label>
        <label>
          <input
            type="checkbox"
            checked={reflectVertical}
            onChange={(e) => setReflectVertical(e.target.checked)}
          />
          Reflect Vertical
        </label>
        <label>
          <input
            type="checkbox"
            checked={reflectHorizontal}
            onChange={(e) => setReflectHorizontal(e.target.checked)}
          />
          Reflect Horizontal
        </label>
        <label>
          <input
            type="checkbox"
            checked={reflectDiagonal1}
            onChange={(e) => setReflectDiagonal1(e.target.checked)}
          />
          Reflect Diagonal 1
        </label>
        <label>
          <input
            type="checkbox"
            checked={reflectDiagonal2}
            onChange={(e) => setReflectDiagonal2(e.target.checked)}
          />
          Reflect Diagonal 2
        </label>
      </div>
    </div>
  );
};

export default SquareSymmetry;
