import { drawLighterSquare } from './drawingUtils';

export function rotateSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number, angle: number) {
  const frames = 60;
  let currentFrame = 0;

  function animate() {
    ctx.save();
    ctx.translate(x, y);
    ctx.rotate((currentFrame / frames) * (angle * Math.PI / 180));
    ctx.translate(-x, -y);
    drawLighterSquare(ctx, x, y, size);
    ctx.restore();

    currentFrame++;

    if (currentFrame <= frames) {
      requestAnimationFrame(animate);
    }
  }

  animate();
}

export function reflectSquare(ctx: CanvasRenderingContext2D, x: number, y: number, size: number, axis: 'vertical' | 'horizontal' | 'diagonal1' | 'diagonal2') {
  ctx.save();

  switch (axis) {
    case 'vertical':
      ctx.transform(-1, 0, 0, 1, 2 * x, 0);
      break;
    case 'horizontal':
      ctx.transform(1, 0, 0, -1, 0, 2 * y);
      break;
    case 'diagonal1':
      ctx.transform(0, 1, 1, 0, 0, 0);
      break;
    case 'diagonal2':
      ctx.transform(0, -1, -1, 0, 2 * x, 2 * y);
      break;
  }

  drawLighterSquare(ctx, x, y, size);
  ctx.restore();
}
