import { drawLighterSquare } from './drawingUtils';

export function rotateSquare(
  ctx: CanvasRenderingContext2D, 
  size: number, 
  angle: number,
  x?: number,
  y?: number
) {
  const frames = 60;
  let currentFrame = 0;

  const canvasWidth = ctx.canvas.width;
  const canvasHeight = ctx.canvas.height;
  const squareCenterX = x !== undefined ? x + size / 2 : canvasWidth / 2;
  const squareCenterY = y !== undefined ? y + size / 2 : canvasHeight / 2;

  function animate() {
    ctx.clearRect(0, 0, canvasWidth, canvasHeight);

    // Draw the center dot
    ctx.save();
    ctx.fillStyle = 'red';
    ctx.beginPath();
    ctx.arc(squareCenterX, squareCenterY, 2, 0, Math.PI * 2);
    ctx.fill();
    ctx.restore();

    // Rotate around the center of the square
    ctx.save();
    ctx.translate(squareCenterX, squareCenterY); // Move the origin to the center of the square
    ctx.rotate((currentFrame / frames) * (angle * Math.PI / 180)); // Apply the rotation
    ctx.translate(-size / 2, -size / 2); // Move the square back by half its size to center it
    drawLighterSquare(ctx, 0, 0, size); // Draw the square with its top-left at (0, 0)
    ctx.restore();

    currentFrame++;

    if (currentFrame <= frames) {
      requestAnimationFrame(animate);
    }
  }

  animate();
}
